import React, { useState } from 'react';
import { Address } from './types';  
import AddressForm from './components/AddressForm';
import AddressList from './components/AddressList';
import Modal from './components/modal';


export interface Address {
  cep: string;
  logradouro: string;
  complemento: string;
  bairro: string;
  localidade: string;
  uf: string;
}


const App: React.FC = () => {
  const [addresses, setAddresses] = useState<Address[]>(
    JSON.parse(localStorage.getItem('addresses') || '[]').map((addr: Partial<Address>) => ({
      cep: addr.cep || '',
      logradouro: addr.logradouro || '',
      complemento: addr.complemento || '',  // ✅ Garante que sempre terá complemento
      bairro: addr.bairro || '',
      localidade: addr.localidade || '',
      uf: addr.uf || '',
    }))
  );

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingAddress, setEditingAddress] = useState<Address | null>(null);

  const handleSave = (address: Address) => {
    const fullAddress = { ...address, complemento: address.complemento || '' }; 

    let updatedAddresses;
    if (editingAddress) {
      updatedAddresses = addresses.map((addr) =>
        addr.cep === editingAddress.cep ? fullAddress : addr
      );
    } else {
      updatedAddresses = [...addresses, fullAddress];
    }

    setAddresses(updatedAddresses);
    localStorage.setItem('addresses', JSON.stringify(updatedAddresses));
    setIsModalOpen(false);
    setEditingAddress(null);
  };


  const handleDelete = (cep: string) => {
    const updatedAddresses = addresses.filter((addr) => addr.cep !== cep);
    setAddresses(updatedAddresses);
    localStorage.setItem('addresses', JSON.stringify(updatedAddresses));
  };

  const handleEdit = (address: Address) => {
    setEditingAddress({
      ...address,
      complemento: address.complemento || '',
    });
    setIsModalOpen(true);
  };


  return (
    <div className="min-h-screen flex flex-col items-center p-6 bg-gray-100">
      <h1 className="text-2xl font-bold mb-4">Endereços Salvos</h1>
      <button
        onClick={() => {
          setEditingAddress(null);
          setIsModalOpen(true);
        }}
        className="mb-4 bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-600"
      >
        Adicionar Endereço
      </button>
      <AddressList addresses={addresses} onDelete={handleDelete} onEdit={handleEdit} />
      {isModalOpen && (
        <Modal onClose={() => setIsModalOpen(false)}>
          <AddressForm onSave={handleSave} initialData={editingAddress} />
        </Modal>
      )}
    </div>
  );
};

export default App;
