import React, { useState } from 'react';
import axios from 'axios';
import InputMask from 'react-input-mask';

interface Address {
  cep: string;
  logradouro: string;
  bairro: string;
  localidade: string;
  uf: string;
}

interface AddressFormProps {
  onSave: (address: Address) => void;
}

const AddressForm: React.FC<AddressFormProps> = ({ onSave }) => {
  const [cep, setCep] = useState('');
  const [address, setAddress] = useState<Address | null>(null);
  const [loading, setLoading] = useState(false);

  const fetchAddress = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`https://viacep.com.br/ws/${cep.replace(/\D/g, '')}/json/`);
      setAddress(response.data);
    } catch (error) {
      console.error('Erro ao consultar CEP:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleBlur = () => {
    if (cep.replace(/\D/g, '').length === 8) {
      fetchAddress();
    }
  };

  const handleSave = () => {
    if (address) {
      onSave(address);
    }
  };

  const handleClear = () => {
    setCep('');
    setAddress(null);
  };

  return (
    <div className="p-4 bg-white shadow-md rounded-lg">
      <h2 className="text-xl font-bold mb-4">Consultar Endere√ßo</h2>
      <div className="mb-4">
        <label htmlFor="cep" className="block text-sm font-medium text-gray-700">
          CEP
        </label>
        <InputMask
          mask="99999-999"
          id="cep"
          type="text"
          value={cep}
          onChange={(e) => setCep(e.target.value)}
          onBlur={handleBlur}
          placeholder="Digite o CEP"
          className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
        />
      </div>
      {loading && <p>Carregando...</p>}
      {address && (
        <>
          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700">Logradouro</label>
            <input
              type="text"
              value={address.logradouro}
              readOnly
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm bg-gray-100 cursor-not-allowed"
            />
          </div>
          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700">Bairro</label>
            <input
              type="text"
              value={address.bairro}
              readOnly
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm bg-gray-100 cursor-not-allowed"
            />
          </div>
          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700">Cidade</label>
            <input
              type="text"
              value={address.localidade}
              readOnly
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm bg-gray-100 cursor-not-allowed"
            />
          </div>
          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700">Estado</label>
            <input
              type="text"
              value={address.uf}
              readOnly
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm bg-gray-100 cursor-not-allowed"
            />
          </div>
          <div className="flex justify-between">
            <button
              onClick={handleSave}
              className="bg-indigo-600 text-white py-2 px-4 rounded-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
            >
              Salvar
            </button>
            <button
              onClick={handleClear}
              className="bg-red-500 text-white py-2 px-4 rounded-md hover:bg-red-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
            >
              Limpar
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default AddressForm;
