import React, { useState, useEffect } from 'react';
import AddressForm from './components/AddressForm';
import AddressList from './components/AddressList';
import useAddressCache from './hooks/useAddressCache';

const App: React.FC = () => {
  const [addresses, setAddresses] = useState<any[]>([]);
  const { getCachedAddress, cacheAddress } = useAddressCache();

  // Carregar endereços salvos do localStorage ao iniciar
  useEffect(() => {
    const savedAddresses = localStorage.getItem('addresses');
    if (savedAddresses) {
      setAddresses(JSON.parse(savedAddresses));
    }
  }, []);

  // Salvar endereços no localStorage sempre que o estado mudar
  useEffect(() => {
    localStorage.setItem('addresses', JSON.stringify(addresses));
  }, [addresses]);

  const handleSave = (address: any) => {
    setAddresses((prev) => [...prev, address]);
    cacheAddress(address.cep, address);
  };

  return (
    <div className="max-w-3xl mx-auto p-4">
      <h1 className="text-3xl font-bold text-center mb-6">Consulta de Endereços</h1>
      <AddressForm onSave={handleSave} />
      <AddressList addresses={addresses} />
    </div>
  );
};

export default App;
